import UIKit

// MARK: - Connect View, Interactor, and Presenter
extension AboutShowViewController: AboutShowPresenterOutput {
    override func prepare(for segue: UIStoryboardSegue, sender: Any?) {
        router.passDataToNextScene(segue: segue)
    }
}

extension AboutShowInteractor: AboutShowViewControllerOutput {
}

extension AboutShowPresenter: AboutShowInteractorOutput {
}

class AboutShowConfigurator {
    // MARK: - Properties
    static let sharedInstance = AboutShowConfigurator()
    
    
    // MARK: - Class Initialization
    private init() {}
    

    // MARK: - Custom Functions
    func configure(viewController: AboutShowViewController) {
        let router = AboutShowRouter()
        router.viewController = viewController
        
        let presenter = AboutShowPresenter()
        presenter.output = viewController
        
        let interactor = AboutShowInteractor()
        interactor.output = presenter
        
        viewController.output = interactor
        viewController.router = router
    }
}
