import UIKit

// MARK: - Input & Output protocols
protocol AboutShowInteractorInput {
    func doSomething(request: AboutShow.Something.Request)
}

protocol AboutShowInteractorOutput {
    func presentSomething(response: AboutShow.Something.Response)
}

class AboutShowInteractor: AboutShowInteractorInput {
    // MARK: - Properties
    var output: AboutShowInteractorOutput!
    var worker: AboutShowWorker!
    
    
    // MARK: - Custom Functions. Business logic
    func doSomething(request: AboutShow.Something.Request) {
        // NOTE: Create some Worker to do the work
        worker = AboutShowWorker()
        worker.doSomeWork()
        
        // NOTE: Pass the result to the Presenter
        let response = AboutShow.Something.Response()
        output.presentSomething(response: response)
    }
}
