import UIKit

// MARK: - Connect View, Interactor, and Presenter
extension BusinessCardCreateViewController: BusinessCardCreatePresenterOutput {
    override func prepare(for segue: UIStoryboardSegue, sender: Any?) {
        router.passDataToNextScene(segue: segue)
    }
}

extension BusinessCardCreateInteractor: BusinessCardCreateViewControllerOutput {
}

extension BusinessCardCreatePresenter: BusinessCardCreateInteractorOutput {
}

class BusinessCardCreateConfigurator {
    // MARK: - Properties
    static let sharedInstance = BusinessCardCreateConfigurator()
    
    
    // MARK: - Class initialization
    private init() {}
    

    // MARK: - Custom Functions
    func configure(viewController: BusinessCardCreateViewController) {
        let router                  =   BusinessCardCreateRouter()
        router.viewController       =   viewController
        
        let presenter               =   BusinessCardCreatePresenter()
        presenter.viewController    =   viewController
        
        let interactor              =   BusinessCardCreateInteractor()
        interactor.presenter        =   presenter
        
        viewController.interactor   =   interactor
        viewController.router       =   router
    }
}
