import UIKit

// MARK: - Connect View, Interactor, and Presenter
extension BusinessCardShowViewController: BusinessCardShowPresenterOutput {
    override func prepare(for segue: UIStoryboardSegue, sender: Any?) {
        router.passDataToNextScene(segue: segue)
    }
}

extension BusinessCardShowInteractor: BusinessCardShowViewControllerOutput {
}

extension BusinessCardShowPresenter: BusinessCardShowInteractorOutput {
}

class BusinessCardShowConfigurator {
    // MARK: - Properties
    static let sharedInstance = BusinessCardShowConfigurator()
    
    
    // MARK: - Class initialization
    private init() {}
    

    // MARK: - Custom Functions
    func configure(viewController: BusinessCardShowViewController) {
        let router                  =   BusinessCardShowRouter()
        router.viewController       =   viewController
        
        let presenter               =   BusinessCardShowPresenter()
        presenter.viewController    =   viewController
        
        let interactor              =   BusinessCardShowInteractor()
        interactor.presenter        =   presenter
        
        viewController.interactor   =   interactor
        viewController.router       =   router
    }
}
