import UIKit

// MARK: - Input protocols for current Interactor component VIP-cicle
protocol BusinessCardShowInteractorInput {
    func businessCardDidDelete(withRequestModel requestModel: BusinessCardShowModels.Item.RequestModel)
}

// MARK: - Output protocols for Presenter component VIP-cicle
protocol BusinessCardShowInteractorOutput {
    func businessCardDidPrepareToShowDelete(fromResponseModel responseModel: BusinessCardShowModels.Item.ResponseModel)
}

class BusinessCardShowInteractor: BusinessCardShowInteractorInput {
    // MARK: - Properties
    var presenter: BusinessCardShowInteractorOutput!
    var worker: BusinessCardShowWorker!
    
    
    // MARK: - Custom Functions. Business logic
    func businessCardDidDelete(withRequestModel requestModel: BusinessCardShowModels.Item.RequestModel) {
        // NOTE: Pass the result to the Presenter
        MSMRestApiManager.instance.userRequestDidRun(.userDeleteBusinessCard(requestModel.parameters, false), withHandlerResponseAPICompletion:  { responseAPI in
            let businessCardResponseModel = BusinessCardShowModels.Item.ResponseModel(responseAPI: responseAPI)
            self.presenter.businessCardDidPrepareToShowDelete(fromResponseModel: businessCardResponseModel)
        })
    }
}
