import UIKit

// MARK: - Input protocols for current Presenter component VIP-cicle
protocol BusinessCardShowPresenterInput {
    func businessCardDidPrepareToShowDelete(fromResponseModel responseModel: BusinessCardShowModels.Item.ResponseModel)
}

// MARK: - Output protocols for ViewController component VIP-cicle
protocol BusinessCardShowPresenterOutput: class {
    func businessCardDidShowDelete(fromViewModel viewModel: BusinessCardShowModels.Item.ViewModel)
}

class BusinessCardShowPresenter: BusinessCardShowPresenterInput {
    // MARK: - Properties
    weak var viewController: BusinessCardShowPresenterOutput!
    
    
    // MARK: - Custom Functions. Presentation logic
    func businessCardDidPrepareToShowDelete(fromResponseModel responseModel: BusinessCardShowModels.Item.ResponseModel) {
        guard responseModel.responseAPI != nil else {
            let businessCardViewModel = BusinessCardShowModels.Item.ViewModel(status: "RESPONSE_NIL")
            self.viewController.businessCardDidShowDelete(fromViewModel: businessCardViewModel)
            
            return
        }

        // NOTE: Format the response from the Interactor and pass the result back to the View Controller
        let businessCardViewModel = BusinessCardShowModels.Item.ViewModel(status: (responseModel.responseAPI?.status)!)
        viewController.businessCardDidShowDelete(fromViewModel: businessCardViewModel)
    }
}
