import UIKit

// MARK: - Input & Output protocols
protocol BusinessCardShowRouterInput {
    func navigateToBusinessCardCreateScene(withBusinessCardID businessCardID: String?)
}

class BusinessCardShowRouter: BusinessCardShowRouterInput {
    // MARK: - Properties
    weak var viewController: BusinessCardShowViewController!
    
    
    // MARK: - Custom Functions. Navigation
    func navigateToBusinessCardCreateScene(withBusinessCardID businessCardID: String?) {
        let storyboard = UIStoryboard(name: "BusinessCardCreate", bundle: nil)
        let businessCardCreateVC = storyboard.instantiateViewController(withIdentifier: "BusinessCardCreateVC") as! BusinessCardCreateViewController
        businessCardCreateVC.businessCardID = businessCardID
        
        viewController.navigationController?.pushViewController(businessCardCreateVC, animated: true)
    }

    
    // Communication
    func passDataToNextScene(segue: UIStoryboardSegue) {
        // NOTE: Teach the router which scenes it can communicate with
        if segue.identifier == "ShowSomewhereScene" {
            passDataToSomewhereScene(segue: segue)
        }
    }
    
    // Transition
    func passDataToSomewhereScene(segue: UIStoryboardSegue) {
        // NOTE: Teach the router how to pass data to the next scene
        // let someWhereViewController = segue.destinationViewController as! SomeWhereViewController
        // someWhereViewController.output.name = viewController.output.name
    }
}
