import UIKit

// MARK: - Connect View, Interactor, and Presenter
extension CalendarShowViewController: CalendarShowPresenterOutput {
    override func prepare(for segue: UIStoryboardSegue, sender: Any?) {
        router.passDataToNextScene(segue: segue)
    }
}

extension CalendarShowInteractor: CalendarShowViewControllerOutput {
}

extension CalendarShowPresenter: CalendarShowInteractorOutput {
}

class CalendarShowConfigurator {
    // MARK: - Properties
    static let sharedInstance = CalendarShowConfigurator()
    
    
    // MARK: - Class initialization
    private init() {}
    

    // MARK: - Custom Functions
    func configure(viewController: CalendarShowViewController) {
        let router = CalendarShowRouter()
        router.viewController = viewController
        
        let presenter = CalendarShowPresenter()
        presenter.output = viewController
        
        let interactor = CalendarShowInteractor()
        interactor.output = presenter
        
        viewController.output = interactor
        viewController.router = router
    }
}
