import UIKit

// MARK: - Connect View, Interactor, and Presenter
extension CategoriesShowViewController: CategoriesShowPresenterOutput {
    override func prepare(for segue: UIStoryboardSegue, sender: Any?) {
        router.passDataToNextScene(segue: segue)
    }
}

extension CategoriesShowInteractor: CategoriesShowViewControllerOutput {
}

extension CategoriesShowPresenter: CategoriesShowInteractorOutput {
}

class CategoriesShowConfigurator {
    // MARK: - Properties
    static let sharedInstance = CategoriesShowConfigurator()
    
    
    // MARK: - Class initialization
    private init() {}
    

    // MARK: - Custom Functions
    func configure(viewController: CategoriesShowViewController) {
        let router = CategoriesShowRouter()
        router.viewController = viewController
        
        let presenter = CategoriesShowPresenter()
        presenter.output = viewController
        
        let interactor = CategoriesShowInteractor()
        interactor.output = presenter
        
        viewController.output = interactor
        viewController.router = router
    }
}
