import UIKit

// MARK: - Connect View, Interactor, and Presenter
extension DiscountCardShowViewController: DiscountCardShowPresenterOutput {
    override func prepare(for segue: UIStoryboardSegue, sender: Any?) {
        router.passDataToNextScene(segue: segue)
    }
}

extension DiscountCardShowInteractor: DiscountCardShowViewControllerOutput {
}

extension DiscountCardShowPresenter: DiscountCardShowInteractorOutput {
}

class DiscountCardShowConfigurator {
    // MARK: - Properties
    static let sharedInstance = DiscountCardShowConfigurator()
    
    
    // MARK: - Class initialization
    private init() {}
    

    // MARK: - Custom Functions
    func configure(viewController: DiscountCardShowViewController) {
        let router                  =   DiscountCardShowRouter()
        router.viewController       =   viewController
        
        let presenter               =   DiscountCardShowPresenter()
        presenter.viewController    =   viewController
        
        let interactor              =   DiscountCardShowInteractor()
        interactor.presenter        =   presenter
        
        viewController.interactor   =   interactor
        viewController.router       =   router
    }
}
