import UIKit

// MARK: - Connect View, Interactor, and Presenter
extension FavoriteShowViewController: FavoriteShowPresenterOutput {
    override func prepare(for segue: UIStoryboardSegue, sender: Any?) {
        router.passDataToNextScene(segue: segue)
    }
}

extension FavoriteShowInteractor: FavoriteShowViewControllerOutput {
}

extension FavoriteShowPresenter: FavoriteShowInteractorOutput {
}

class FavoriteShowConfigurator {
    // MARK: - Properties
    static let sharedInstance = FavoriteShowConfigurator()
    
    
    // MARK: - Class Initialization
    private init() {}
    

    // MARK: - Custom Functions
    func configure(viewController: FavoriteShowViewController) {
        let router = FavoriteShowRouter()
        router.viewController = viewController
        
        let presenter = FavoriteShowPresenter()
        presenter.output = viewController
        
        let interactor = FavoriteShowInteractor()
        interactor.output = presenter
        
        viewController.output = interactor
        viewController.router = router
    }
}
