import UIKit

// MARK: - Connect View, Interactor, and Presenter
extension FavoriteServicesShowViewController: FavoriteServicesShowPresenterOutput {
    override func prepare(for segue: UIStoryboardSegue, sender: Any?) {
        router.passDataToNextScene(segue: segue)
    }
}

extension FavoriteServicesShowInteractor: FavoriteServicesShowViewControllerOutput {
}

extension FavoriteServicesShowPresenter: FavoriteServicesShowInteractorOutput {
}

class FavoriteServicesShowConfigurator {
    // MARK: - Properties
    static let sharedInstance = FavoriteServicesShowConfigurator()
    
    
    // MARK: - Class initialization
    private init() {}
    

    // MARK: - Custom Functions
    func configure(viewController: FavoriteServicesShowViewController) {
        let router                  =   FavoriteServicesShowRouter()
        router.viewController       =   viewController
        
        let presenter               =   FavoriteServicesShowPresenter()
        presenter.viewController    =   viewController
        
        let interactor              =   FavoriteServicesShowInteractor()
        interactor.presenter        =   presenter
        
        viewController.interactor   =   interactor
        viewController.router       =   router
    }
}
