import UIKit

// MARK: - Input & Output protocols
protocol HandbookShowRouterInput {
    func navigateToOrganizationMapShowScene(withItems items: [PointAnnotationBinding])
}

class HandbookShowRouter: HandbookShowRouterInput {
    // MARK: - Properties
    weak var viewController: HandbookShowViewController!
    
    
    // MARK: - Custom Functions. Navigation
    func navigateToOrganizationMapShowScene(withItems items: [PointAnnotationBinding]) {
        let storyboard = UIStoryboard(name: "OrganizationsMapShow", bundle: nil)
        let organizationsMapShowVC = storyboard.instantiateViewController(withIdentifier: "OrganizationsMapShowVC") as! OrganizationsMapShowViewController
        organizationsMapShowVC.items = items
        organizationsMapShowVC.isTapGestureAvailable = true
        
        viewController.navigationController?.pushViewController(organizationsMapShowVC, animated: true)
        
        // Handler back button
        organizationsMapShowVC.handlerPassDataCompletion = { locationItem in
            self.viewController.locationManager.currentLocation = locationItem as! LocationItem
        }
    }
    
    // Communication
    func passDataToNextScene(segue: UIStoryboardSegue) {
        // NOTE: Teach the router which scenes it can communicate with
        if segue.identifier == "ShowSomewhereScene" {
            passDataToSomewhereScene(segue: segue)
        }
    }
    
    // Transition
    func passDataToSomewhereScene(segue: UIStoryboardSegue) {
        // NOTE: Teach the router how to pass data to the next scene
        // let someWhereViewController = segue.destinationViewController as! SomeWhereViewController
        // someWhereViewController.output.name = viewController.output.name
    }
}
