import UIKit

// MARK: - Connect View, Interactor, and Presenter
extension HandbooksShowViewController: HandbooksShowPresenterOutput {
    override func prepare(for segue: UIStoryboardSegue, sender: Any?) {
        router.passDataToNextScene(segue: segue)
    }
}

extension HandbooksShowInteractor: HandbooksShowViewControllerOutput {
}

extension HandbooksShowPresenter: HandbooksShowInteractorOutput {
}

class HandbooksShowConfigurator {
    // MARK: - Properties
    static let sharedInstance = HandbooksShowConfigurator()
    
    
    // MARK: - Class initialization
    private init() {}
    

    // MARK: - Custom Functions
    func configure(viewController: HandbooksShowViewController) {
        let router                  =   HandbooksShowRouter()
        router.viewController       =   viewController
        
        let presenter               =   HandbooksShowPresenter()
        presenter.viewController    =   viewController
        
        let interactor              =   HandbooksShowInteractor()
        interactor.presenter        =   presenter
        
        viewController.interactor   =   interactor
        viewController.router       =   router
    }
}
