import UIKit

// MARK: - Input & Output protocols
protocol NewsShowPresenterInput {
    func presentSomething(response: NewsShow.Something.Response)
}

protocol NewsShowPresenterOutput: class {
    func displaySomething(viewModel: NewsShow.Something.ViewModel)
}

class NewsShowPresenter: NewsShowPresenterInput {
    // MARK: - Properties
    weak var output: NewsShowPresenterOutput!
    
    
    // MARK: - Custom Functions. Presentation logic
    func presentSomething(response: NewsShow.Something.Response) {
        // NOTE: Format the response from the Interactor and pass the result back to the View Controller
        let viewModel = NewsShow.Something.ViewModel()
        output.displaySomething(viewModel: viewModel)
    }
}
