import UIKit

// MARK: - Connect View, Interactor, and Presenter
extension NewsActionsShowViewController: NewsActionsShowPresenterOutput {
    override func prepare(for segue: UIStoryboardSegue, sender: Any?) {
        router.passDataToNextScene(segue: segue)
    }
}

extension NewsActionsShowInteractor: NewsActionsShowViewControllerOutput {
}

extension NewsActionsShowPresenter: NewsActionsShowInteractorOutput {
}

class NewsActionsShowConfigurator {
    // MARK: - Properties
    static let sharedInstance = NewsActionsShowConfigurator()
    
    
    // MARK: - Class initialization
    private init() {}
    

    // MARK: - Custom Functions
    func configure(viewController: NewsActionsShowViewController) {
        let router                  =   NewsActionsShowRouter()
        router.viewController       =   viewController
        
        let presenter               =   NewsActionsShowPresenter()
        presenter.viewController    =   viewController
        
        let interactor              =   NewsActionsShowInteractor()
        interactor.presenter        =   presenter
        
        viewController.interactor   =   interactor
        viewController.router       =   router
    }
}
