import UIKit

// MARK: - Connect View, Interactor, and Presenter
extension NewsDataShowViewController: NewsDataShowPresenterOutput {
    override func prepare(for segue: UIStoryboardSegue, sender: Any?) {
        router.passDataToNextScene(segue: segue)
    }
}

extension NewsDataShowInteractor: NewsDataShowViewControllerOutput {
}

extension NewsDataShowPresenter: NewsDataShowInteractorOutput {
}

class NewsDataShowConfigurator {
    // MARK: - Properties
    static let sharedInstance = NewsDataShowConfigurator()
    
    
    // MARK: - Class initialization
    private init() {}
    

    // MARK: - Custom Functions
    func configure(viewController: NewsDataShowViewController) {
        let router                  =   NewsDataShowRouter()
        router.viewController       =   viewController
        
        let presenter               =   NewsDataShowPresenter()
        presenter.viewController    =   viewController
        
        let interactor              =   NewsDataShowInteractor()
        interactor.presenter        =   presenter
        
        viewController.interactor   =   interactor
        viewController.router       =   router
    }
}
