import UIKit

// MARK: - Connect View, Interactor, and Presenter
extension OmnieCardsShowViewController: OmnieCardsShowPresenterOutput {
    override func prepare(for segue: UIStoryboardSegue, sender: Any?) {
        router.passDataToNextScene(segue: segue)
    }
}

extension OmnieCardsShowInteractor: OmnieCardsShowViewControllerOutput {
}

extension OmnieCardsShowPresenter: OmnieCardsShowInteractorOutput {
}

class OmnieCardsShowConfigurator {
    // MARK: - Properties
    static let sharedInstance = OmnieCardsShowConfigurator()
    
    
    // MARK: - Class Initialization
    private init() {}
    

    // MARK: - Custom Functions
    func configure(viewController: OmnieCardsShowViewController) {
        let router = OmnieCardsShowRouter()
        router.viewController = viewController
        
        let presenter = OmnieCardsShowPresenter()
        presenter.output = viewController
        
        let interactor = OmnieCardsShowInteractor()
        interactor.output = presenter
        
        viewController.output = interactor
        viewController.router = router
    }
}
