import UIKit

// MARK: - Connect View, Interactor, and Presenter
extension OrderShowViewController: OrderShowPresenterOutput {
    override func prepare(for segue: UIStoryboardSegue, sender: Any?) {
        router.passDataToNextScene(segue: segue)
    }
}

extension OrderShowInteractor: OrderShowViewControllerOutput {
}

extension OrderShowPresenter: OrderShowInteractorOutput {
}

class OrderShowConfigurator {
    // MARK: - Properties
    static let sharedInstance = OrderShowConfigurator()
    
    
    // MARK: - Class initialization
    private init() {}
    

    // MARK: - Custom Functions
    func configure(viewController: OrderShowViewController) {
        let router                  =   OrderShowRouter()
        router.viewController       =   viewController
        
        let presenter               =   OrderShowPresenter()
        presenter.viewController    =   viewController
        
        let interactor              =   OrderShowInteractor()
        interactor.presenter        =   presenter
        
        viewController.interactor   =   interactor
        viewController.router       =   router
    }
}
