import UIKit

// MARK: - Connect View, Interactor, and Presenter
extension OrganizationShowViewController: OrganizationShowPresenterOutput {
    override func prepare(for segue: UIStoryboardSegue, sender: Any?) {
        router.passDataToNextScene(segue: segue)
    }
}

extension OrganizationShowInteractor: OrganizationShowViewControllerOutput {
}

extension OrganizationShowPresenter: OrganizationShowInteractorOutput {
}

class OrganizationShowConfigurator {
    // MARK: - Properties
    static let sharedInstance = OrganizationShowConfigurator()
    
    
    // MARK: - Class initialization
    private init() {}
    

    // MARK: - Custom Functions
    func configure(viewController: OrganizationShowViewController) {
        let router                  =   OrganizationShowRouter()
        router.viewController       =   viewController
        
        let presenter               =   OrganizationShowPresenter()
        presenter.viewController    =   viewController
        
        let interactor              =   OrganizationShowInteractor()
        interactor.presenter        =   presenter
        
        viewController.interactor   =   interactor
        viewController.router       =   router
    }
}
