import UIKit

// MARK: - Connect View, Interactor, and Presenter
extension OrganizationsMapShowViewController: OrganizationsMapShowPresenterOutput {
    override func prepare(for segue: UIStoryboardSegue, sender: Any?) {
        router.passDataToNextScene(segue: segue)
    }
}

extension OrganizationsMapShowInteractor: OrganizationsMapShowViewControllerOutput {
}

extension OrganizationsMapShowPresenter: OrganizationsMapShowInteractorOutput {
}

class OrganizationsMapShowConfigurator {
    // MARK: - Properties
    static let sharedInstance = OrganizationsMapShowConfigurator()
    
    
    // MARK: - Class initialization
    private init() {}
    

    // MARK: - Custom Functions
    func configure(viewController: OrganizationsMapShowViewController) {
        let router                  =   OrganizationsMapShowRouter()
        router.viewController       =   viewController
        
        let presenter               =   OrganizationsMapShowPresenter()
        presenter.viewController    =   viewController
        
        let interactor              =   OrganizationsMapShowInteractor()
        interactor.presenter        =   presenter
        
        viewController.interactor   =   interactor
        viewController.router       =   router
    }
}
