import UIKit

// MARK: - Connect View, Interactor, and Presenter
extension PersonalPageShowViewController: PersonalPageShowPresenterOutput {
    override func prepare(for segue: UIStoryboardSegue, sender: Any?) {
        router.passDataToNextScene(segue: segue)
    }
}

extension PersonalPageShowInteractor: PersonalPageShowViewControllerOutput {
}

extension PersonalPageShowPresenter: PersonalPageShowInteractorOutput {
}

class PersonalPageShowConfigurator {
    // MARK: - Properties
    static let sharedInstance = PersonalPageShowConfigurator()
    
    
    // MARK: - Class Initialization
    private init() {}
    

    // MARK: - Custom Functions
    func configure(viewController: PersonalPageShowViewController) {
        let router = PersonalPageShowRouter()
        router.viewController = viewController
        
        let presenter = PersonalPageShowPresenter()
        presenter.output = viewController
        
        let interactor = PersonalPageShowInteractor()
        interactor.output = presenter
        
        viewController.output = interactor
        viewController.router = router
    }
}
