import UIKit

// MARK: - Connect View, Interactor, and Presenter
extension SettingsShowViewController: SettingsShowPresenterOutput {
    override func prepare(for segue: UIStoryboardSegue, sender: Any?) {
        router.passDataToNextScene(segue: segue)
    }
}

extension SettingsShowInteractor: SettingsShowViewControllerOutput {
}

extension SettingsShowPresenter: SettingsShowInteractorOutput {
}

class SettingsShowConfigurator {
    // MARK: - Properties
    static let sharedInstance = SettingsShowConfigurator()
    
    
    // MARK: - Class initialization
    private init() {}
    

    // MARK: - Custom Functions
    func configure(viewController: SettingsShowViewController) {
        let router = SettingsShowRouter()
        router.viewController = viewController
        
        let presenter = SettingsShowPresenter()
        presenter.output = viewController
        
        let interactor = SettingsShowInteractor()
        interactor.output = presenter
        
        viewController.output = interactor
        viewController.router = router
    }
}
