import UIKit

// MARK: - Connect View, Interactor, and Presenter
extension SignInShowViewController: SignInShowPresenterOutput {
    override func prepare(for segue: UIStoryboardSegue, sender: Any?) {
        navigationController?.isNavigationBarHidden = true

        router.passDataToNextScene(segue: segue)
    }
}

extension SignInShowInteractor: SignInShowViewControllerOutput {
}

extension SignInShowPresenter: SignInShowInteractorOutput {
}

class SignInShowConfigurator {
    // MARK: - Properties
    static let sharedInstance = SignInShowConfigurator()
    
    
    // MARK: - Class Initialization
    private init() {}
    

    // MARK: - Custom Functions
    func configure(viewController: SignInShowViewController) {
        let router = SignInShowRouter()
        router.viewController = viewController
        
        let presenter = SignInShowPresenter()
        presenter.output = viewController
        
        let interactor = SignInShowInteractor()
        interactor.output = presenter
        
        viewController.output = interactor
        viewController.router = router
    }
}
